mapper.select:
select
from qhc_article where 1=1
and article_type = #{articleType,jdbcType=VARCHAR}
and article_title like concat('%',#{articleTitle,jdbcType=VARCHAR},'%')
order by create_time DESC
limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

select CAST(article_id AS CHAR) as articleId, article_title as articleTitle, article_brief as articleBrief, article_image as articleImage from qhc_article where 1=1
and article_type = #{articleType,jdbcType=VARCHAR}
and article_title like '%${searchText}%'
order by create_time desc
limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

SELECT count(*) FROM qhc_article where 1=1
and article_type = #{articleType,jdbcType=VARCHAR}
and article_title like concat('%',#{articleTitle,jdbcType=VARCHAR},'%')

select

from qhc_article
where article_type=#{type,jdbcType=VARCHAR} order by create_time DESC limit 0,#{count,jdbcType=NUMERIC}
