mapper.select:
select
cloud_work_labor.*,cloud_work_vip.weights weights,cloud_work_vip.validity_time validity_time
from cloud_work_labor
left join cloud_work_vip on cloud_work_vip.work_id = cloud_work_labor.id and cloud_work_vip.work_type=3

	AND cloud_work_vip.validity_time >= #{validityTime,jdbcType=DATE}

where


		(cloud_work_labor.work_need like concat('%',#{searchText,jdbcType=VARCHAR},'%')
		or cloud_work_labor.contacts_name like concat('%',#{searchText,jdbcType=VARCHAR},'%')
		or cloud_work_labor.mobile like concat('%',#{searchText,jdbcType=VARCHAR},'%'))

			and cloud_work_labor.status = #{status,jdbcType=NUMERIC}


			and cloud_work_labor.create_user = #{createUser,jdbcType=NUMERIC}



		1=1

			and cloud_work_labor.work_need like concat('%',#{workNeed,jdbcType=VARCHAR},'%')


			and cloud_work_labor.contacts_name like concat('%',#{contactsName,jdbcType=VARCHAR},'%')


			and cloud_work_labor.mobile like concat('%',#{mobile,jdbcType=VARCHAR},'%')


			and cloud_work_labor.status = #{status,jdbcType=NUMERIC}


			and cloud_work_labor.create_user = #{createUser,jdbcType=NUMERIC}








				order by cloud_work_vip.${sortName} ${orderName}


				order by cloud_work_labor.${sortName} ${orderName}




		order by cloud_work_labor.update_time desc





	limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

SELECT count(*) from cloud_work_labor left join cloud_work_vip on cloud_work_vip.work_id = cloud_work_labor.id and cloud_work_vip.work_type=3 AND cloud_work_vip.validity_time >= #{validityTime,jdbcType=DATE}
where
(cloud_work_labor.work_need like concat('%',#{searchText,jdbcType=VARCHAR},'%') or cloud_work_labor.contacts_name like concat('%',#{searchText,jdbcType=VARCHAR},'%') or cloud_work_labor.mobile like concat('%',#{searchText,jdbcType=VARCHAR},'%'))
and cloud_work_labor.status = #{status,jdbcType=NUMERIC}
and cloud_work_labor.create_user = #{createUser,jdbcType=NUMERIC}
1=1
and cloud_work_labor.work_need like concat('%',#{workNeed,jdbcType=VARCHAR},'%')
and cloud_work_labor.contacts_name like concat('%',#{contactsName,jdbcType=VARCHAR},'%')
and cloud_work_labor.mobile like concat('%',#{mobile,jdbcType=VARCHAR},'%')
and cloud_work_labor.status = #{status,jdbcType=NUMERIC}
and cloud_work_labor.create_user = #{createUser,jdbcType=NUMERIC}
