mapper.select:
select
cloud_work_person.*,cloud_work_vip.weights weights,cloud_work_vip.validity_time validity_time
from cloud_work_person
left join cloud_work_vip
on cloud_work_vip.work_id = cloud_work_person.id
and cloud_work_vip.work_type=1

	AND cloud_work_vip.validity_time >= #{validityTime,jdbcType=DATE}

where


		(cloud_work_person.work_intent like concat('%',#{searchText,jdbcType=VARCHAR},'%')
		or cloud_work_person.contacts_name like concat('%',#{searchText,jdbcType=VARCHAR},'%')
		or cloud_work_person.mobile like concat('%',#{searchText,jdbcType=VARCHAR},'%'))

			and cloud_work_person.status = #{status,jdbcType=NUMERIC}


			and cloud_work_person.create_user = #{createUser,jdbcType=NUMERIC}



		1=1

			and cloud_work_person.work_intent like concat('%',#{workIntent,jdbcType=VARCHAR},'%')


			and cloud_work_person.contacts_name like concat('%',#{contactsName,jdbcType=VARCHAR},'%')


			and cloud_work_person.mobile like concat('%',#{mobile,jdbcType=VARCHAR},'%')


			and cloud_work_person.status = #{status,jdbcType=NUMERIC}


			and cloud_work_person.create_user = #{createUser,jdbcType=NUMERIC}







				order by cloud_work_vip.${sortName} ${orderName}


				order by cloud_work_person.${sortName} ${orderName}




		order by cloud_work_person.update_time desc



	limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

SELECT count(*) from cloud_work_person left join cloud_work_vip on cloud_work_vip.work_id = cloud_work_person.id and cloud_work_vip.work_type=1 AND cloud_work_vip.validity_time >= #{validityTime,jdbcType=DATE}
where
(cloud_work_person.work_intent like concat('%',#{searchText,jdbcType=VARCHAR},'%') or cloud_work_person.contacts_name like concat('%',#{searchText,jdbcType=VARCHAR},'%') or cloud_work_person.mobile like concat('%',#{searchText,jdbcType=VARCHAR},'%'))
and cloud_work_person.status = #{status,jdbcType=NUMERIC}
and cloud_work_person.create_user = #{createUser,jdbcType=NUMERIC}
1=1
and cloud_work_person.work_intent like concat('%',#{workIntent,jdbcType=VARCHAR},'%')
and cloud_work_person.contacts_name like concat('%',#{contactsName,jdbcType=VARCHAR},'%')
and cloud_work_person.mobile like concat('%',#{mobile,jdbcType=VARCHAR},'%')
and cloud_work_person.status = #{status,jdbcType=NUMERIC}
and cloud_work_person.create_user = #{createUser,jdbcType=NUMERIC}
