mapper.select:
select
cloud_work_recruitment.*,cloud_work_vip.weights weights,cloud_work_vip.validity_time validity_time
from cloud_work_recruitment
left join cloud_work_vip on cloud_work_vip.work_id = cloud_work_recruitment.id and cloud_work_vip.work_type=2

	AND cloud_work_vip.validity_time >= #{validityTime,jdbcType=DATE}

where


		(cloud_work_recruitment.work_type like concat('%',#{searchText,jdbcType=VARCHAR},'%')
		or cloud_work_recruitment.contacts_name like concat('%',#{searchText,jdbcType=VARCHAR},'%')
		or cloud_work_recruitment.mobile like concat('%',#{searchText,jdbcType=VARCHAR},'%'))

			and cloud_work_recruitment.status = #{status,jdbcType=NUMERIC}


			and cloud_work_recruitment.create_user = #{createUser,jdbcType=NUMERIC}



		1=1

			and cloud_work_recruitment.work_type like concat('%',#{workType,jdbcType=VARCHAR},'%')


			and cloud_work_recruitment.company like concat('%',#{company,jdbcType=VARCHAR},'%')


			and cloud_work_recruitment.contacts_name like concat('%',#{contactsName,jdbcType=VARCHAR},'%')


			and cloud_work_recruitment.mobile like concat('%',#{mobile,jdbcType=VARCHAR},'%')


			and cloud_work_recruitment.status = #{status,jdbcType=NUMERIC}


			and cloud_work_recruitment.create_user = #{createUser,jdbcType=NUMERIC}







				order by cloud_work_vip.${sortName} ${orderName}


				order by cloud_work_recruitment.${sortName} ${orderName}




		order by cloud_work_recruitment.update_time desc



	limit #{offset,jdbcType=INTEGER}, #{limit,jdbcType=INTEGER}

SELECT count(*) from cloud_work_recruitment left join cloud_work_vip on cloud_work_vip.work_id = cloud_work_recruitment.id and cloud_work_vip.work_type=2
AND cloud_work_vip.validity_time >= #{validityTime,jdbcType=DATE}
where
(cloud_work_recruitment.work_type like concat('%',#{searchText,jdbcType=VARCHAR},'%') or cloud_work_recruitment.contacts_name like concat('%',#{searchText,jdbcType=VARCHAR},'%') or cloud_work_recruitment.mobile like concat('%',#{searchText,jdbcType=VARCHAR},'%'))
and cloud_work_recruitment.status = #{status,jdbcType=NUMERIC}
and cloud_work_recruitment.create_user = #{createUser,jdbcType=NUMERIC}
1=1
and cloud_work_recruitment.work_type like concat('%',#{workType,jdbcType=VARCHAR},'%')
and cloud_work_recruitment.company like concat('%',#{company,jdbcType=VARCHAR},'%')
and cloud_work_recruitment.contacts_name like concat('%',#{contactsName,jdbcType=VARCHAR},'%')
and cloud_work_recruitment.mobile like concat('%',#{mobile,jdbcType=VARCHAR},'%')
and cloud_work_recruitment.status = #{status,jdbcType=NUMERIC}
and cloud_work_recruitment.create_user = #{createUser,jdbcType=NUMERIC}
